#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="D:\Coding\Studium\wt-beiboot\node_modules\.pnpm\nice-color-palettes@1.0.1\node_modules\nice-color-palettes\bin\node_modules;D:\Coding\Studium\wt-beiboot\node_modules\.pnpm\nice-color-palettes@1.0.1\node_modules\nice-color-palettes\node_modules;D:\Coding\Studium\wt-beiboot\node_modules\.pnpm\nice-color-palettes@1.0.1\node_modules;D:\Coding\Studium\wt-beiboot\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/d/Coding/Studium/wt-beiboot/node_modules/.pnpm/nice-color-palettes@1.0.1/node_modules/nice-color-palettes/bin/node_modules:/mnt/d/Coding/Studium/wt-beiboot/node_modules/.pnpm/nice-color-palettes@1.0.1/node_modules/nice-color-palettes/node_modules:/mnt/d/Coding/Studium/wt-beiboot/node_modules/.pnpm/nice-color-palettes@1.0.1/node_modules:/mnt/d/Coding/Studium/wt-beiboot/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../../bin/index.js" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../../bin/index.js" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../../bin/index.js" $args
  } else {
    & "node$exe"  "$basedir/../../bin/index.js" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
